import MasterPage from '../components/MasterPage';
import fetch from 'isomorphic-unfetch';
import Precio from '../components/Precio';
import News from '../components/News';

const Index = props => (
    <MasterPage>
        <div className='row'>
            <div className='col-12'>
                <h2>Precio del bitcoin</h2>
                <Precio
                precio={props.precioBitcoin}
                />
            </div>
            <div className='col-md-8'>
            {console.log(props.news)}
                <News 
                news={props.news}
                />
            </div>
            <div className='col-md-4'>
                <h2>Pr√≥ximos eventos</h2>
            </div>
        </div>
    </MasterPage>
);

Index.getInitialProps = async () => {
    const precio = await fetch('https://pro-api.coinmarketcap.com/v1/cryptocurrency/quotes/latest?symbol=BIT&CMC_PRO_API_KEY=959f5f4f-cf55-4666-95c7-48778c878863');
    const news = await fetch('https://newsapi.org/v2/everything?q=bitcoin&from=2019-11-26&sortBy=publishedAt&apiKey=39a9575bf2c8463a9a139fd02e495797');

    const respPrecio = await precio.json();
    const respNews = await news.json();

    return {
        precioBitcoin: respPrecio.data.BIT.quote.USD,
        newsBitcoin: respNews.articles
    }
}

export default Index;
